import { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
  Link,
} from "@material-ui/core";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader/CardHeader";
import formatSlug from "../../utils/formatSlug";
import useStyles from "./styles";

import Like from "../Like";
import { PostContext } from "../../context/postContext";

const PostCard = ({ item }) => {
  const { updateLiked, handleLike } = useContext(PostContext);
  const classes = useStyles();

  /* eslint-disable */
  useEffect(() => {
    // updateLiked(item);
  }, [item]);
  /* eslint-anable */
  return (
    <Card className={classes.root}>
      <Cardheader item={item} />
      <CardHeader title={item.title} subheader={formatDate(item.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={item.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {item.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <Like item={item} onLike={handleLike} />
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Link href={`/posts/${formatSlug(item.title)}/${item.id}`}>
          <Button size="small" color="primary">
            Read
          </Button>
        </Link>
        <p>{getReadingTime(item.body)}</p>
      </CardActions>
    </Card>
  );
};

export default PostCard;

PostCard.propTypes = {
  item: PropTypes.object.isRequired,
};
