import PropTypes from "prop-types";
import { Card, Button, Link } from "@material-ui/core";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader";
import useStyles from "./styles";
import { useContext } from "react";
import PostContext from "../../context/postContext";

export default function PostCard({ post, author, loading }) {
  const { getSelectedPost } = useContext(PostContext);
  const classes = useStyles();
  return (
    <Card className={classes.root}>
      <Cardheader post={post} />
      <CardHeader
        title={post.title}
        subheader={formatDate(post.date_created)}
      />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={post.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {post.body}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Link
          component="button"
          variant="body2"
          onClick={() => {
            console.info("I'm a button.");
          }}
        >
          Button Link
        </Link>
        <Button
          size="small"
          color="primary"
          onClick={(e) => {
            e.preventDefault();
            getSelectedPost(post);
          }}
          href={`/posts/${post.title}`}
        >
          Read
        </Button>
        <p>{getReadingTime(post.body)}</p>
      </CardActions>
    </Card>
  );
}

PostCard.propTypes = {
  post: PropTypes.object,
  author: PropTypes.object,
  loading: PropTypes.bool,
};
