import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
} from "@material-ui/core";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader";
import formatSlug from "../../utils/formatSlug";
import useStyles from "./styles";

import auth from "../../services/authService";
import Like from "../Like";
import usePostsState from "../../hooks/usePostsState";

const PostCard = ({ post, onLike, onDisLike }) => {
  const [state, , setSelectedPost] = usePostsState();
  const [currentUser, setCurrentUser] = useState({});
  const classes = useStyles();

  useEffect(() => {
    setCurrentUser(auth.getCurrentUser());
  }, []);

  const handleLike = (post) => {
    if (post.like_user_id.includes(currentUser.id))
      onDisLike(currentUser.id, post.id);
    else onLike(currentUser.id, post.id);
  };

  console.log("setSElected: ", setSelectedPost);

  const handleClick = (e) => {
    e.preventDefault();

    setSelectedPost(post);

    if (state.selectedPost)
      window.location = `/posts/${state.selectedPost.title}/${state.selectedPost.id}`;
  };

  return (
    <Card className={classes.root}>
      <Cardheader post={post} />
      <CardHeader title={post.title} subheader={formatDate(post.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={post.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {post.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <Like item={post} currentUser={currentUser} handleLike={handleLike} />
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Button size="small" color="primary" onClick={handleClick}>
          Read
        </Button>
        <p>{getReadingTime(post.body)}</p>
      </CardActions>
    </Card>
  );
};

export default PostCard;

PostCard.propTypes = {
  post: PropTypes.object.isRequired,
  onLike: PropTypes.func.isRequired,
  onDisLike: PropTypes.func.isRequired,
};
