import http from "./httpService";

export const registerUser = async (user) => {
  user.email_verified = false;
  try {
    return await http.post("/users/profile/create", user);
  } catch (err) {
    return err.response;
  }
};

export const getUserByEmail = async (email) => {
  try {
    return await http.get("/users/profile", { params: email });
  } catch (err) {
    console.log(err);
  }
};

export const getUserPosts = async (user_id) => {
  try {
    return await http.get("users/user/posts", { params: user_id });
  } catch (err) {
    console.log(err);
  }
};

export const getUserProfileByUsername = async (username) => {
  try {
    return await http.get("/users/profile/username", { params: username });
  } catch (err) {
    console.log(err);
  }
};

export const getAuthorPosts = async (username) => {
  try {
    return await http.get("/users/author/posts", { params: username });
  } catch (err) {
    console.log(err);
  }
};
