import actionTypes from "./action_types";

const loading = () => {
  return {
    type: actionTypes.LOADING,
  };
};

const error = (err) => {
  return {
    type: actionTypes.ERROR,
    payload: err,
  };
};

const loginUser = (user) => {
  return {
    type: actionTypes.LOGIN,
    payload: user,
  };
};

const logoutUser = (user) => {
  return {
    type: actionTypes.LOGOUT,
    payload: user,
  };
};

const userActions = {
  loading,
  error,
  loginUser,
  logoutUser,
};

export default userActions;
