import React, { useEffect, useCallback } from "react";
import { CssBaseline, Typography } from "@material-ui/core";

import usePostsState from "../hooks/usePostsState";
import useSnackState from "../hooks/useSnackState";
import Layout from "../common/Layout";
import GridComponent from "../common/Grid/Grid";
import SnackBar from "../common/SnackBar";
import Loading from "../common/Loading";

const Home = () => {
  const [state] = usePostsState();
  const [open, handleClose, handleOpen] = useSnackState();

  useEffect(() => {
    if (state.error) handleOpen();
  }, [state.error]);

  if (!state.success && state.loading) return <Loading />;
  return (
    <>
      <CssBaseline />
      <Layout>
        <Typography component="div" align="center">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <GridComponent
          items={state.posts}
          onLike={state.handleLike}
          onDisLike={state.handleDisLike}
        />
      </Layout>
      {state.error && (
        <SnackBar
          open={open}
          severity={state.error ? "error" : "success"}
          err={state.error}
          success={state.success}
          onClose={handleClose}
        />
      )}
    </>
  );
};

export default Home;
