import React, { useContext, useEffect } from "react";
import { CssBaseline, Typography } from "@material-ui/core";
import MDEditor from "@uiw/react-md-editor";

import Layout from "../../common/Layout";
import PostContext from "../../context/postContext";
import SmallerContainer from "../../common/SmallerContainer";
import useStyles from "./styles";

export default function PostDetails({ match }) {
  const { posts, getPosts } = useContext(PostContext);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  const selectedPost = posts.filter((post) => post.id === +match.params.id)[0];

  return (
    <>
      <CssBaseline />
      {selectedPost ? (
        <Layout>
          <Typography
            component="div"
            align="center"
            variant="h1"
            className={classes.title}
          >
            {selectedPost.title}
          </Typography>
          <Typography component="div" align="center" variant="subtitle1">
            {selectedPost.sub_title}
          </Typography>
          <SmallerContainer>
            <Typography component="div" variant="body1">
              <MDEditor.Markdown source={selectedPost.body} />
            </Typography>
          </SmallerContainer>
        </Layout>
      ) : (
        <p>Something failed</p>
      )}
    </>
  );
}
