import { useFormik } from "formik";
import * as Yup from "yup";
import { Button } from "@material-ui/core";
import { TextField } from "@material-ui/core";
// import UserContext from "../context/userContext";
// import { UseForm } from "../hooks/useForm";

const Home = () => {
  // const { user, loading, errors, register } = useContext(UserContext);

  const RegisterSchema = Yup.object().shape({
    first_name: Yup.string("Enter your first name")
      .max(20, "First Name should be of maximum 20 characters length")
      .required("First name is required"),
    last_name: Yup.string("Enter your last name")
      .max(20, "First Name should be of maximum 20 characters length")
      .required("Last Name is required"),
    email: Yup.string("Enter your email")
      .email("Enter a valid email")
      .required("Email is required"),
    password: Yup.string("Enter your password")
      .min(6, "Password should be of minimum 6 characters length")
      .required("Password is required"),
  });

  const handleSubmit = (values) => {
    register(values);
  };

  return (
    <div>
      <h1>Register Form</h1>
      <Formik
        initialValues={{
          first_name: "",
          last_name: "",
          email: "",
          password: "",
        }}
        validationSchema={RegisterSchema}
        onSubmit={(values) => handleSubmit(values)}
      >
        {({ errors, touched }) => (
          <Form>
            <Field name="first_name" />
            {errors.first_name && touched.first_name ? (
              <div>{errors.first_name}</div>
            ) : null}
