export const initialState = {
  user: null,
  is_authenticated: false,
};

export const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case "login":
      return {
        ...state,
        user: payload,
        is_authenticated: true,
      };
    case "logout":
      return {
        ...state,
        is_authenticated: false,
      };
    case "signup":
      return {
        ...state,
        user: payload,
        is_authenticated: true,
      };
    default:
      return state;
  }
};
