import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "100%",
    maxHeight: "100%",
    position: "relative",
    margin: "0",
    padding: "0",
  },
  profileHeader: {
    display: "flex",
    flexDirection: "column",
    alignContent: "center",
    justifyContent: "center",
    alignItems: "center",
    flexShrink: "1",
    maxWidth: "100%",
    maxHeight: "100%",
    marign: "0",
    padding: "0",
  },
  coverPhotoContainer: {
    backgroundColor: "#dddd",
    display: "flex",
    flexDirection: "column",
    alignContent: "center",
    justifyContent: "center",
    alignItems: "center",
    flexShrink: "1",
    justifySelf: "center",
    maxHeight: "500px",
    maxWidth: "100%",
    padding: "0",
  },
  img: {
    width: "auto",
    height: "400px",
    objectFit: "cover",
  },
  avatarContainer: {
    marginTop: "-4%",
    width: "160px",
    height: "160px",
  },
}));

export default useStyles;
