import React, { useContext, useEffect } from "react";
import { CssBaseline, Typography } from "@material-ui/core";
import MDEditor from "@uiw/react-md-editor";

import Layout from "../../common/Layout";
import SmallerContainer from "../../common/SmallerContainer";
import Alert from "../../common/Alert";
import useStyles from "./styles";
import { PostContext } from "../../context/postContext";
import Spiner from "../../common/Spiner";
import Like from "../../common/Like";
import SnackBar from "../../common/SnackBar";
import useSnackState from "../../hooks/useSnackState";

export default function PostDetails({ match }) {
  const { posts, error, success, loading, handleLike } =
    useContext(PostContext);
  const [open, handleClose, handleOpen] = useSnackState();
  const classes = useStyles();

  /* eslint-disable */
  useEffect(() => {
    handleOpen();
  }, [error]);

  const selectedPost = posts.filter((post) => post.id === +match.params.id)[0];

  if (loading) return <Spiner />;

  if (error)
    return <Alert errMessage={error} severity={error ? "error" : null} />;
  return (
    <>
      <CssBaseline />
      {selectedPost && (
        <Layout>
          <Typography
            component="div"
            align="center"
            variant="h1"
            className={classes.title}
          >
            {selectedPost.title}
          </Typography>
          <Typography
            component="div"
            align="center"
            variant="h5"
            className={classes.subTitle}
          >
            {selectedPost.sub_title}
          </Typography>
          <Like item={selectedPost} onLike={handleLike} />
          <SmallerContainer>
            <Typography component="div" variant="body1">
              <MDEditor.Markdown source={selectedPost.body} />
            </Typography>
          </SmallerContainer>
          <SnackBar
            open={open}
            err={error}
            severity={error ? "error" : "success"}
            onClose={handleClose}
          />
        </Layout>
      )}
    </>
  );
}
