import React, { useContext, useEffect } from "react";
import {
  Container,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostContext from "../context/postContext";
import AlertBar from "../common/Alert";
import Loading from "../common/Loading";
import Grid from "../common/Grid/Grid";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
  gridContainer: {
    alignContent: "stretch",
    alignItems: "stretch",
  },
  gridItems: {
    alignSelf: "auto",
  },
}));
const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  if (error)
    return (
      <AlertBar
        errMessage={error}
        title={error}
        severity={error ? "error" : "warning"}
      />
    );

  if (!success && loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <Grid items={posts} loading={loading} />
      </Container>
    </>
  );
};

export default Home;
