import React, { useContext, useEffect } from "react";
import {
  Container,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostContext from "../context/postContext";
import Loading from "../common/Loading";
import GridComponent from "../common/Grid/Grid";
import SnackBar from "../common/SnackBar";
import useSnackState from "../hooks/useSnackState";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
}));

const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const [open, handleClose, handleOpen] = useSnackState();
  const classes = useStyles();

  useEffect(() => {
    getPosts();
    handleOpen();
  }, []);

  if (!success && loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <GridComponent items={posts} loading={loading} />
      </Container>
      {(error || success) && (
        <SnackBar
          open={open}
          security={error ? "error" : "success"}
          err={error}
          success={success}
          onClose={handleClose}
        />
      )}
    </>
  );
};

export default Home;
