import React, { useContext, useEffect } from "react";
import { CardHeader, Avatar, IconButton, Link } from "@material-ui/core";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import PropTypes from "prop-types";

import UserContext from "../../context/userContext";
import formatDate from "../../utils/formatDate";
import useStyles from "./styles";

export default function Cardheader({ item }) {
  const { user, getUserById } = useContext(UserContext);
  const classes = useStyles();

  /* eslint-disable  */
  useEffect(() => {
    getUserById(item.author);
  }, []);
  /* eslint-enable */

  return (
    <>
      {user && (
        <CardHeader
          avatar={
            <Link href={`/users/user/${user.last_name}`}>
              <Avatar aria-label="recipe" className={classes.avatar}>
                {user.last_name ? user.last_name[0] : null}
              </Avatar>
            </Link>
          }
          action={
            <IconButton aria-label="settings">
              <MoreVertIcon />
            </IconButton>
          }
          title={"@" + user.last_name}
          subheader={formatDate(user.created)}
        />
      )}
    </>
  );
}

Cardheader.propTypes = {
  item: PropTypes.object.isRequired,
};
