import React, { useState, useContext } from "react";
import * as Yup from "yup";
import MDEditor from "@uiw/react-md-editor";
import { TextField, Button, makeStyles } from "@material-ui/core";
import SnackBar from "../common/SnackBar";
import PostContext from "../context/postContext";
import "../index.less";
import UseForm from "../hooks/useForm";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

export default function PostForm() {
  const { addPost, loading, success } = useContext(PostContext);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [value, setValue] = useState("");

  const schema = Yup.object().shape({
    title: Yup.string().min(5).max(255).required().label("Title"),
  });

  const handleClick = () => {
    setOpen(true);
  };

  const handleSubmit = (values) => {
    console.log("values submited", values);
    // addPost({ ...values, body: value });
    handleClick();
  };

  const values = { title: "" };
  const formik = UseForm(values, handleSubmit, schema);

  const classes = useStyles();

  const handleClose = (event, reason) => {
    if (reason === "clickaway") return setOpen(false);
  };
  return (
    <div className="content">
      <form onSubmit={formik.handleSubmit} className={classes.root}>
        <TextField
          fullWidth
          id="title"
          type="text"
          name="title"
          label="Title"
          value={formik.values.title}
          onChange={formik.handleChange}
          error={formik.touched.title && Boolean(formik.errors.title)}
          helperText={formik.touched.title && formik.errors.title}
        />
        <MDEditor value={value} onChange={setValue} />
        <Button color="primary" variant="contained" fullWidth type="submit">
          {loading ? "Loading..." : "Submit"}
        </Button>
      </form>
      <SnackBar
        message={error}
        severity={error ? "error" : "success"}
        success={success}
        open={open}
        onClose={handleClose}
      />
    </div>
  );
}
