import actionTypes from "./action_types";

const loading = () => {
  return {
    type: actionTypes.LOADING,
  };
};

const error = (err) => {
  return {
    type: actionTypes.ERROR,
    payload: err,
  };
};

const postData = (data) => {
  return {
    type: actionTypes.POST,
    payload: data,
  };
};

const updateUser = (user) => {
  return {
    type: actionTypes.UPDATE,
    payload: user,
  };
};

const deleteUser = (user) => {
  return {
    type: actionTypes.DELETE,
    payload: user,
  };
};

const getData = (user) => {
  return {
    type: actionTypes.GET,
    payload: user,
  };
};

const loginUser = (user) => {
  return {
    type: actionTypes.LOGIN,
    payload: user,
  };
};

const logoutUser = (user) => {
  return {
    type: actionTypes.LOGOUT,
    payload: user,
  };
};

const addPost = (item) => {
  return {
    type: actionTypes.POST,
    payload: item,
  };
};

const getPosts = (item) => {
  return {
    type: actionTypes.GET,
    payload: item,
  };
};

const getUser = (item) => {
  return {
    type: actionTypes.GET,
    palyload: item,
  };
};

const actions = {
  loading,
  error,
  postData,
  updateUser,
  deleteUser,
  loginUser,
  logoutUser,
  getData,
  addPost,
  getPosts,
  getUser,
};

export default actions;
