import { useContext } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
  Link,
} from "@material-ui/core";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader/CardHeader";
import formatSlug from "../../utils/formatSlug";
import useStyles from "./styles";

import Like from "../Like";
import { PostContext } from "../../context/postContext";
import history from "../../utils/history";

const PostCard = ({ item }) => {
  const { setSelectedPost } = useContext(PostContext);
  const classes = useStyles();

  const handlePostSelection = (post) => {
    setSelectedPost(post);

    history.location = `/posts/${formatSlug(post.title)}/${post.id}`;
  };
  return (
    <Card className={classes.root}>
      <Cardheader item={item} />
      <CardHeader title={item.title} subheader={formatDate(item.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={item.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {item.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <Like item={item} />
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Link>
          <Button
            size="small"
            color="primary"
            onClick={() => handlePostSelection(item)}
          >
            Read
          </Button>
        </Link>
        <p>{getReadingTime(item.body)}</p>
      </CardActions>
    </Card>
  );
};

export default PostCard;

PostCard.propTypes = {
  item: PropTypes.object.isRequired,
};
