import * as ACTION_TYPES from "../actions/action_types";

export const initialState = {
  user: {
    first_name: "Mojib",
    last_name: "Mohammad",
    email: "example@gamil.com",
    password: "123456",
  },
  is_authenticated: false,
  loading: false,
  errors: {},
};

export const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case ACTION_TYPES.LOADING:
      return {
        ...state,
        user: null,
        is_authenticated: false,
        loading: true,
        errors: null,
      };
    case ACTION_TYPES.ERROR:
      return {
        ...state,
        user: null,
        is_authenticated: false,
        loading: false,
        errors: payload,
      };
    case ACTION_TYPES.REGISTER:
      return {
        ...state,
        user: payload,
        is_authenticated: true,
        loading: false,
        errors: false,
      };
    case ACTION_TYPES.LOGIN:
      return {
        ...state,
        user: payload,
        is_authenticated: true,
        loading: false,
        errors: false,
      };
    case ACTION_TYPES.LOGOUT:
      return {
        ...state,
        user: null,
        is_authenticated: false,
        loading: false,
        errors: false,
      };
    default:
      return state;
  }
};
