import React, { useContext, useEffect } from "react";
import {
  Container,
  Grid,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostCard from "../common/Card/index";
import PostContext from "../context/postContext";
import AlertBar from "../common/Alert";
import Loading from "../common/Loading";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
  gridContainer: {
    display: "grid",
    alignItems: "stretch",
  },
  gridItems: {
    alignSelf: "stretch",
    justifySelf: "stretch",
  },
}));
const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  if (error)
    return (
      <AlertBar
        errMessage={error}
        title={error}
        severity={error ? "error" : "warning"}
      />
    );

  if (!success && loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <Grid container spacing={2} className={classes.gridContainer}>
          {posts.map((post) => (
            <Grid item xs={3} key={post.id} className={classes.gridItems}>
              <PostCard post={post} loading={loading} />
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
};

export default Home;
