import actionTypes from "../actions/action_types";

export const initialState = {
  posts: [],
  selected: {},
  loading: false,
  success: false,
  error: null,
};

export const postReducer = (state, action) => {
  switch (action.type) {
    case actionTypes.LOADING:
      return {
        ...state,
        loading: true,
        success: false,
        error: null,
      };
    case actionTypes.POST:
      return {
        ...state,
        posts: action.payload,
        loading: false,
        success: true,
        error: null,
      };
    case actionTypes.ERROR:
      return {
        ...state,
        posts: [],
        loading: false,
        success: false,
        error: action.payload,
      };
    case actionTypes.GET:
      return {
        ...state,
        loadding: false,
        success: true,
        error: null,
        posts: action.payload,
      };
    case actionTypes.SELECTED:
      return {
        ...state,
        selected: action.payload,
        loading: false,
        error: null,
        success: false,
      };
    default:
      return state;
  }
};
