import React, { useState, useContext } from "react";
import * as Yup from "yup";
import MDEditor from "@uiw/react-md-editor";
import { Button, makeStyles } from "@material-ui/core";
import SnackBar from "../common/SnackBar";
import PostContext from "../context/postContext";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

export default function PostForm() {
  const { addPost, err, loading, success } = useContext(PostContext);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [value, setValue] = useState("");
  const [title, setTitle] = useState("");
  const classes = useStyles();

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === "clickaway") return setOpen(false);
  };

  const handleTitleChange = async ({ currentTarget: input }) => {
    await schema.validate({ title: value, body: input.value });

    setTitle(input.value);
  };

  const schema = Yup.object().shape({
    title: Yup.string().min(20).max(255).required().label("Ttitle"),
    body: Yup.string().min(50).required().label("Body"),
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    handleClick();
  };

  return (
    <div className="content">
      <h1>Post Form</h1>
      <form
        className={classes.root}
        style={{ width: 500, margin: "auto" }}
        onSubmit={handleSubmit}
      >
        <label htmlFor="title">Title*</label>
        <input
          type="text"
          name="title"
          label="Title"
          id="title"
          placeholder="Enter post title here"
          value={title}
          onChange={handleTitleChange}
        />
        <MDEditor value={value} onChange={setValue} />
        {/* <MDEditor.Markdown source={value} /> */}
        <Button color="primary" variant="contained" fullWidth type="submit">
          {loading ? "loading..." : "Submit"}
        </Button>
      </form>
      <SnackBar
        message={error}
        severity={error ? "error" : "success"}
        success={success}
        open={open}
        onClose={handleClose}
      />
    </div>
  );
}
