import React, { useReducer } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { TextField, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core";
import Snack from "../common/Snack";
import { authReducer, initialSate } from "../store/reducers/authReducer";
import auth from "../services/authService";
import action_types from "../store/actions/action_types";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

const LoginForm = () => {
  const [state, dispatch] = useReducer(authReducer, initialSate);
  const classes = useStyles();

  const schema = Yup.object().shape({
    email: Yup.string().email().required().label("Email"),
    password: Yup.string().min(6).required().label("password"),
  });

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: schema,
    onSubmit: (values) => {
      try {
        dispatch({ type: action_types.LOADING });
        auth.login(values.email, values.password);
        const { state } = this.props.location;
        window.location = state ? state.from.pathname : "/";
      } catch (err) {
        return dispatch({ type: action_types.ERROR, payload: err });
      }
    },
  });

  return (
    <div className="content">
      <h1>Login Form</h1>
      <Snack error={state.error} success={state.success} />
      <form
        onSubmit={formik.handleSubmit}
        className={classes.root}
        style={{ width: "500px", margin: "auto" }}
      >
        <TextField
          fullWidth
          id="email"
          name="email"
          label="Email"
          value={formik.values.email}
          onChange={formik.handleChange}
          error={formik.touched.email && Boolean(formik.errors.email)}
          required
          helperText={formik.touched.email && formik.errors.email}
        />
        <TextField
          fullWidth
          id="password"
          name="password"
          label="Password"
          value={formik.values.password}
          onChange={formik.handleChange}
          error={formik.touched.password && Boolean(formik.errors.password)}
          required
          helperText={formik.touched.password && formik.errors.password}
          autoComplete="current-password"
        />
        <Button color="primary" variant="contained" fullWidth type="submit">
          {state.loading ? "loading..." : "Login"}
        </Button>
      </form>
    </div>
  );
};

export default LoginForm;
