import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import UserProvider from "./store/providers/UserProvider";

import Home from "./pages/Home";
import RegisterForm from "./components/RegisterForm";
import LoginForm from "./components/LoginForm";
import PostForm from "./components/PostForm";
import PostProvider from "./store/providers/PostProvider";
import SnackBar from "./common/SnackBar";
import useSnackState from "./hooks/useSnackState";
import "./App.css";

const App = () => {
  const [open, handleClose] = useSnackState();
  return (
    <div>
      <UserProvider>
        <PostProvider>
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/login" component={LoginForm} />
            <Route path="/register" component={RegisterForm} />
            <Route path="/add-post" component={PostForm} />
            <Route path="/posts" component={Home} />
            <Redirect to="/" from="/posts" />
          </Switch>
        </PostProvider>
      </UserProvider>
      <SnackBar
        open={open}
        security={error ? "error" : "success"}
        err={error}
        success={success}
        onClose={handleClose}
      />
    </div>
  );
};

export default App;
