import React, { useContext } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { TextField, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core";
import UserContext from "../context/userContext";
import SnackBar from "../common/SnackBar";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

const RegisterForm = () => {
  const { loading, success, error, register } = useContext(UserContext);
  const classes = useStyles();

  const schema = Yup.object().shape({
    first_name: Yup.string().min(4).max(20).required().label("First Name"),
    last_name: Yup.string().min(4).max(20).required().label("Last Name"),
    email: Yup.string().email().required().label("Email"),
    password: Yup.string().min(6).required().label("password"),
  });

  const formik = useFormik({
    initialValues: {
      first_name: "",
      last_name: "",
      email: "",
      password: "",
    },
    validationSchema: schema,
    onSubmit: (values) => {
      register(values);
    },
  });

  return (
    <div className="content">
      <h1>Register Form</h1>
      <Snack error={error} success={success} />
      {/* <Form formik={formik} classes={classes} loading={loading} /> */}
      <form
        onSubmit={formik.handleSubmit}
        className={classes.root}
        style={{ width: "500px", margin: "auto" }}
      >
        <TextField
          fullWidth
          id="first_name"
          name="first_name"
          label="First Name"
          type="name"
          value={formik.values["name"]}
          onChange={formik.handleChange}
          error={formik.touched.first_name && Boolean(formik.errors.first_name)}
          required
          helperText={formik.touched.first_name && formik.errors.first_name}
        />
        <TextField
          fullWidth
          id="last_name"
          name="last_name"
          label="Last Name"
          type="name"
          value={formik.values.last_name}
          onChange={formik.handleChange}
          error={formik.touched.last_name && Boolean(formik.errors.last_name)}
          required
          helperText={formik.touched.last_name && formik.errors.last_name}
        />
        <TextField
          fullWidth
          id="email"
          name="email"
          label="Email"
          type="email"
          value={formik.values.email}
          onChange={formik.handleChange}
          error={formik.touched.email && Boolean(formik.errors.email)}
          required
          helperText={formik.touched.email && formik.errors.email}
        />
        <TextField
          fullWidth
          id="password"
          name="password"
          label="Password"
          type="password"
          value={formik.values.password}
          onChange={formik.handleChange}
          error={formik.touched.password && Boolean(formik.errors.password)}
          required
          helperText={formik.touched.password && formik.errors.password}
        />
        <Button color="primary" variant="contained" fullWidth type="submit">
          {loading ? "loading..." : "Register"}
        </Button>
      </form>
    </div>
  );
};

export default RegisterForm;
