import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
} from "@material-ui/core";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader";
import formatSlug from "../../utils/formatSlug";
import useStyles from "./styles";

import auth from "../../services/authService";

export default function PostCard({ post, onLike }) {
  const [currentUser, setCurrentUser] = useState({});
  const classes = useStyles();

  useEffect(() => {
    setCurrentUser(auth.getCurrentUser());
  }, []);

  console.log("post like", currentUser);

  return (
    <Card className={classes.root}>
      <Cardheader post={post} />
      <CardHeader title={post.title} subheader={formatDate(post.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={post.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {post.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <IconButton
          aria-label="add to favorites"
          color={
            post.like_user_id.includes(currentUser.id) ? "secondary" : "inhiret"
          }
          onClick={() => onLike(auth.getCurrentUser().id, post.id)}
        >
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Button
          size="small"
          color="primary"
          href={`/posts/${formatSlug(post.title)}/${post.id}`}
        >
          Read
        </Button>
        <p>{getReadingTime(post.body)}</p>
      </CardActions>
    </Card>
  );
}

PostCard.propTypes = {
  post: PropTypes.object.isRequired,
  onLike: PropTypes.func.isRequired,
};
