import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    [theme.breakpoints.down("lg")]: {
      width: "40%",
      flexBasis: "40%",
    },
    alignContent: "stretch",
    alignItems: "stretch",
  },
  gridItems: {
    alignSelf: "auto",
  },
  title: {
    marginBottom: "10%",
  },
}));

export default useStyles;
