import { Formik, Form } from "formik";

const UseForm = ({ values, handleSubmit, schema, formTitle, children }) => {
  return (
    <div className="">
      <h1>{formTitle}</h1>
      <Formik
        initialValues={values}
        validationSchema={schema}
        onSubmit={(values, { setSubmitting }) => {
          handleSubmit(values);
          setSubmitting(false);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            {children}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default UseForm;
