import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    maxHeight: "100%",
    position: "relative",
  },
  coverPhotoContainer: {
    maxWidth: "100%",
    maxHeight: "30%",
    margin: "0",
    padding: "0",
  },
  img: {
    width: "100%",
    height: "100%",
    objectFit: "fill",
  },
}));

export default useStyles;
