import React, { useContext, useState } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import UserContext from "../context/userContext";
import { UseForm } from "../hooks/useForm";

const Home = () => {
  const [values, setValues] = UseForm({
    first_name: "",
    last_name: "",
    email: "",
    password: "",
  });
  const { user, loading, errors, register } = useContext(UserContext);

  const RegisterSchema = Yup.object().shape({
    first_name: Yup.string().min(20).required(),
    last_name: Yup.string().min(20).required(),
    email: Yup.string().email("Invalid Email").required(),
    password: Yup.string().min(6).required(),
  });
  // console.log();
  const handleFirstNameChange = ({ currentTarget: input }) => {
    setFirstName(input.value);
  };

