import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    maxHeight: "100%",
    position: "relative",
  },
  coverPhotoContainer: {
    width: "100%",
    maxHeight: "30%",
  },
  img: {
    width: "100%",
    height: "100%",
    objectFit: "fit",
  },
}));

export default useStyles;
