import React, { useState, useContext } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import {
  TextField,
  TextareaAutosize,
  Button,
  makeStyles,
} from "@material-ui/core";
import SnackBar from "../common/SnackBar";
import PostContext from "../context/postContext";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

export default function PostForm() {
  const { addPost, posts, err, loading, success } = useContext(PostContext);
  const [error, setError] = useState(null);
  const classes = useStyles();

  const schema = Yup.object().shape({
    title: Yup.string().min(20).max(255).required().label("Ttitle"),
    body: Yup.string().min(200).required().label("Body"),
  });

  const formik = useFormik({
    initialValues: {
      title: "",
      body: "",
    },
    validationSchema: schema,
    onSubmit: (values) => {
      addPost(values);

      if (err) setError(err);
      console.log(values);
    },
  });

  return (
    <div className="content">
      <h1>Post Form</h1>
      <SnackBar message={error} severity={error ? "error" : "success"} />
      <form
        onSubmit={formik.handleSubmit}
        className={classes.root}
        style={{ width: 500, margin: "auto" }}
      >
        <TextField
          fullWidth
          id="title"
          name="title"
          label="Title"
          type="title"
          value={formik.values.title}
          onChange={formik.handleChange}
          error={formik.touched.title && Boolean(formik.errors.title)}
          required
          helperText={formik.touched.title && formik.errors.title}
          autoComplete="current-title"
        />
        {/* <TextareaAutosize
          id="body"
          name="body"
          label="Body"
          type="textarea"
          maxRows={10}
          minRows={15}
          cols={60}
          onChange={formik.handleChange}
          aria-label="maximum height"
          placeholder="MAXIMUM 255 CHARACTERS"
          required
        /> */}
        <Button color="primary" variant="contained" fullWidth type="submit">
          {loading ? "loading..." : "Submit"}
        </Button>
      </form>
    </div>
  );
}
