import http from "./httpService";

export const getAllPosts = async () => {
  try {
    return await http.get("/posts");
  } catch (err) {
    console.log(err);
  }
};

export const getPostById = async (post_id) => {
  try {
    return await http.get("/posts/post", { params: post_id });
  } catch (err) {
    console.log(err);
  }
};

export const createPost = async (post) => {
  try {
    return await http.post("/posts/create", post);
  } catch (err) {
    console.log(err);
  }
};

export const updatePostById = async (post) => {
  try {
    return await http.put("/posts/post/update", post);
  } catch (err) {
    console.log(err);
  }
};

export const deletePostComments = async (post_id) => {
  try {
    return await http.delete("/posts/post/comments/delete", {
      params: post_id,
    });
  } catch (err) {
    console.log(err);
  }
};

export const deletePost = async (post_id) => {
  try {
    return await http.delete("/posts/post/delete", { params: post_id });
  } catch (err) {
    console.log(err);
  }
};

export const updatePostLikes = async (user_id, post_id) => {
  try {
    return await http.put("/posts/post/likes/update", { user_id, post_id });
  } catch (err) {
    console.log(err);
  }
};

const postService = {
  createPost,
};

export default postService;
