import React, { useReducer } from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import { CssBaseline } from "@material-ui/core";

import UserProvider from "./providers/UserProvider";
import Home from "./pages/Home";
import RegisterForm from "./components/RegisterForm";
import LoginForm from "./components/LoginForm";
import PostForm from "./components/PostForm";
import PostDetails from "./pages/PostDetails/PostDetails";
import { postReducer, initialState } from "./reducers/postReducer";
import postActions from "./actions/postActions";
import postService from "./services/postsService";
import { PostContext } from "./context/postContext";
import "./App.css";

const App = () => {
  const [state, dispatch] = useReducer(postReducer, initialState);

  useEffect(() => {
    getPosts();
  }, []);

  const getPosts = async () => {
    try {
      dispatch(postActions.loading());

      const { data } = await postService.getAllPosts();

      dispatch(postActions.getPosts(data));
    } catch (err) {
      dispatch(postActions.error(err.response || err.message));
    }
  };
  return (
    <div className="main-app-wrapper" id="main-app-wrapper">
      <CssBaseline />
      <UserProvider>
        <PostContext.Provider>
          <Switch>
            <Route path="/login" component={LoginForm} />
            <Route path="/register" component={RegisterForm} />
            <Route path="/add-post" component={PostForm} />
            <Route path="/posts/:title/:id" component={PostDetails} />
            <Route
              exact
              path="/"
              render={(props) => (
                <Home {...props} state={state} dispatch={dispatch} />
              )}
            />
            <Redirect to="/" from="/posts" />
          </Switch>
        </PostContext.Provider>
      </UserProvider>
    </div>
  );
};

export default App;
