import { useReducer } from "react";
import { initialState, userReducer } from "../reducers/userReducer";
import UserContext from "../../context/userContext";
import userService from "../../services/usersService";
import authService from "../../services/authService";
import actionTypes from "../actions/action_types";

const UserProvider = ({ children }) => {
  const [state, dispatch] = useReducer(userReducer, initialState);

  const registerUser = async (user) => {
    try {
      dispatch({ type: actionTypes.LOADING });

      const { data } = await userService.registerUser(user);

      return dispatch({ type: actionTypes.REGISTER, payload: data });
    } catch (err) {
      dispatch({ type: actionTypes.ERROR, payload: err.response });
    }
  };

  const getUserByEmail = async (email) => {
    try {
      dispatch({ type: actionTypes.LOADING });

      const { data } = await userService.getUserByEmail(email);

      return dispatch({ type: actionTypes.GET_USER_BY_EMAIL, payload: data });
    } catch (err) {
      return dispatch({ type: actionTypes.ERROR, payload: err.response });
    }
  };

  const loginUser = (email, password) => {
    return authService.login(email, password);
  };

  const getCurrentUser = () => {
    return authService.getCurrentUser();
  };
  return (
    <UserContext.Provider
      value={{
        user: state.user,
        loading: state.loading,
        success: state.success,
        error: state.error,
        register: registerUser,
        getUserByEmail,
        getCurrentUser,
        loginUser,
      }}
    >
      {children}
    </UserContext.Provider>
  );
};

export default UserProvider;
