import actionTypes from "./action_types";

const loading = () => {
  return {
    type: actionTypes.LOADING,
  };
};

const error = (err) => {
  return {
    type: actionTypes.ERROR,
    payload: err,
  };
};

const addPost = (post) => {
  return {
    type: actionTypes.POST,
    payload: post,
  };
};

const updatePost = (post) => {
  return {
    type: actionTypes.UPDATE,
    payload: post,
  };
};

const deletePost = (post) => {
  return {
    type: actionTypes.DELETE,
    payload: post,
  };
};

const getPosts = (post) => {
  return {
    type: actionTypes.GET,
    payload: post,
  };
};

const like = (like) => {
  return {
    type: actionTypes.LIKE,
    payload: like,
  };
};

const disLike = (like) => {
  return {
    type: actionTypes.DISLIKE,
    payload: like,
  };
};

const postActions = {
  loading,
  error,
  addPost,
  updatePost,
  deletePost,
  getPosts,
  like,
};

export default postActions;
