import React, { useContext, useEffect, useState } from "react";
import {
  Container,
  Grid,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostCard from "../common/Card/index";
import SnackBar from "../common/SnackBar";
import PostContext from "../context/postContext";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
}));
const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const [open, setOpen] = useState(false);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  const handleClose = (event, reason) => {
    if (reason === "clickaway") return;
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };
  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <Grid
          container
          spacing={2}
          justifyContent="center"
          alignItems="stretch"
        >
          {posts.map((post) => (
            <Grid item xs={3}>
              <PostCard key={post.id} post={post} loading={loading} />
            </Grid>
          ))}
        </Grid>
      </Container>
      <SnackBar
        err={error ? error.data : "Something failed!"}
        onClose={handleClose}
      />
    </>
  );
};

export default Home;
