import React, { useContext, useState } from "react";
import UserContext from "../context/userContext";

const Home = () => {
  const [stateUser, setStateUser] = useState({});
  const { user, loading, errors, register } = useContext(UserContext);
  // console.log();
  const handleChange = ({ currentTarget: input }) => {
    const newUser = {
      first_name: input.value,
      last_name: input.value,
      email: input.value,
      password: input.value,
    };
    setStateUser(newUser);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("handleSubmit", stateUser);
    register(stateUser);
  };

  console.log(stateUser);
  return (
    <div>
      Home
      <form action="POST">
        <label htmlFor="first_name">First Name:</label>
        <input
          type="text"
          name="first_name"
          id="first_name"
          placeholder="John"
          onChange={handleChange}
        />
        <label htmlFor="last_name">Last Name:</label>
        <input
          type="text"
          name="last_name"
          id="last_name"
          placeholder="Doe"
          onChange={handleChange}
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          name="email"
          id="email"
          placeholder="Joh_doe@email.com"
          onChange={handleChange}
        />
        <label htmlFor="password">Password</label>
        <input
          type="text"
          name="password"
          id="password"
          placeholder="Doe123@#"
          onChange={handleChange}
        />
        <button type="submit" onClick={handleSubmit}>
          Register
        </button>
      </form>
    </div>
  );
};

export default Home;
