import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
} from "@material-ui/core";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader/CardHeader";
import formatSlug from "../../utils/formatSlug";
import useStyles from "./styles";

import auth from "../../services/authService";
import Like from "../Like";

const PostCard = ({ item, like, onLike, onDisLike }) => {
  const [currentUser, setCurrentUser] = useState({});
  const classes = useStyles();

  useEffect(() => {
    setCurrentUser(auth.getCurrentUser());
  }, []);

  const handleLike = (item) => {
    if (item.like_user_id.includes(currentUser.id)) {
      onDisLike(currentUser.id, item.id);
    } else {
      onLike(currentUser.id, item.id);
    }
  };

  return (
    <Card className={classes.root}>
      <Cardheader item={item} />
      <CardHeader title={item.title} subheader={formatDate(item.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={item.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {item.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <Like
          item={item}
          currentUser={currentUser}
          handleLike={handleLike}
          like={like}
        />
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Button
          size="small"
          color="primary"
          href={`/posts/${formatSlug(item.title)}/${item.id}`}
        >
          Read
        </Button>
        <p>{getReadingTime(item.body)}</p>
      </CardActions>
    </Card>
  );
};

export default PostCard;

PostCard.propTypes = {
  item: PropTypes.object.isRequired,
  onLike: PropTypes.func.isRequired,
  onDisLike: PropTypes.func.isRequired,
};
