import React, { useState, useContext } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import MDEditor from "@uiw/react-md-editor";
import {
  TextField,
  // TextareaAutosize,
  Button,
  makeStyles,
} from "@material-ui/core";
import SnackBar from "../common/SnackBar";
import PostContext from "../context/postContext";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 500,
    },
  },
}));

export default function PostForm() {
  const { addPost, err, loading, success } = useContext(PostContext);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [value, setValue] = useState("");
  const classes = useStyles();

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if ((reason = "clickaway")) return setOpen(false);
  };

  const schema = Yup.object().shape({
    title: Yup.string().min(20).max(255).required().label("Ttitle"),
    body: Yup.string().min(50).required().label("Body"),
  });

  const formik = useFormik({
    initialValues: {
      title: "",
      body: "",
    },
    validationSchema: schema,
    onSubmit: (values) => {
      handleClick();
      addPost(values);

      if (err) {
        setError(err);
      }
    },
  });

  return (
    <div className="content">
      <h1>Post Form</h1>
      <form
        className={classes.root}
        style={{ width: 500, margin: "auto" }}
        onSubmit={formik.handleSubmit}
      >
        <TextField
          fullWidth
          id="title"
          name="title"
          label="Title"
          type="text"
          value={formik.values.title}
          onChange={formik.handleChange}
          error={formik.touched.title && Boolean(formik.errors.title)}
          required
          helperText={formik.touched.title && formik.errors.title}
          autoComplete="current-title"
        />
        <MDEditor value={value} onChange={setValue} />
        {/* <MDEditor.Markdown source={value} /> */}
        {/* <TextareaAutosize
          id="body"
          name="body"
          label="Body"
          type="textarea"
          maxRows={10}
          minRows={15}
          cols={60}
          value={formik.values.body}
          onChange={formik.handleChange}
          aria-label="maximum height"
          placeholder="MAXIMUM 255 CHARACTERS"
          error={formik.touched.title && Boolean(formik.errors.body).toString()}
          required
          autoComplete="current-title"
        /> */}
        <Button color="primary" variant="contained" fullWidth type="submit">
          {loading ? "loading..." : "Submit"}
        </Button>
      </form>
      <SnackBar
        message={error}
        severity={error ? "error" : "success"}
        success={success}
        open={open}
        onClose={handleClose}
      />
    </div>
  );
}
