import action_types, * as ACTION_TYPES from "./action_types";

const loading = () => {
  return {
    type: ACTION_TYPES.LOADING,
  };
};

const error = (err) => {
  return {
    type: ACTION_TYPES.ERROR,
    payload: err,
  };
};

const registerUser = (user) => {
  return {
    type: ACTION_TYPES.REGISTER,
    payload: user,
  };
};

const updateUser = (user) => {
  return {
    type: ACTION_TYPES.UPDATE,
    payload: user,
  };
};

const deleteUser = (user) => {
  return {
    type: ACTION_TYPES.DELETE,
    payload: user,
  };
};

const getUserByEmail = (user) => {
  return {
    type: action_types.GET_USER_BY_EMAIL,
    payload: user,
  };
};

const loginUser = (user) => {
  return {
    type: ACTION_TYPES.LOGIN,
    payload: user,
  };
};

const logoutUser = (user) => {
  return {
    type: ACTION_TYPES.LOGOUT,
    payload: user,
  };
};

export default {
  loading,
  error,
  registerUser,
  updateUser,
  deleteUser,
  loginUser,
  logoutUser,
  getUserByEmail,
};
