import { useFormik } from "formik";

const UseForm = (values, schema, handleSubmit, handleClick) => {
  const formik = useFormik({
    initialValues: values,
    validationSchema: schema,
    onSubmit: (values) => {
      handleClick();
      handleSubmit(values);
    },
  });

  return formik;
};

export default UseForm;
