import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: "100%",
    maxHeight: "100%",
    position: "relative",
    margin: "0",
    padding: "0",
  },
  profileHeader: {
    maxWidth: "100%",
    maxHeight: "100%",
    marign: "0",
    padding: "0",
  },
  coverPhotoContainer: {
    maxWidth: "100%",
    maxHeight: "400px",
    overflow: "hidden",
    margin: "0",
    padding: "0",
  },
  img: {
    width: "auto",
    height: "auto",
    objectFit: "contain",
  },
}));

export default useStyles;
