import { useReducer, useState, useEffect } from "react";
import { PostContext } from "../postContext";
import { postReducer, initialState } from "../reducers/postReducer";
import actions from "../actions/actions";
import postService from "../../services/postsService";
import auth from "../../services/authService";

const PostProvider = ({ children }) => {
  const [{ posts, selectedPost, loading, success, error }, dispatch] =
    useReducer(postReducer, initialState);
  const [currentUser, setCurrentUser] = useState({});

  useEffect(() => {
    setCurrentUser(auth.getCurrentUser());
  }, []);

  const getPosts = async () => {
    dispatch(actions.loading());
    try {
      const { data } = await postService.getAllPosts();

      dispatch(actions.getAll(data));
    } catch (err) {
      dispatch(actions.error(err.response.data || err.message));
    }
  };

  const addPost = async (post) => {
    dispatch(actions.loading());
    try {
      const user = await auth.getCurrentUser();
      post.author = user.id;

      const { data } = await postService.createPost(post);

      dispatch(actions.add(data));
    } catch (err) {
      dispatch(actions.error(err.response.data || err.message));
    }
  };

  const handleLike = async (item) => {
    if (item.liked) {
      try {
        console.log("in if block");

        const { data } = await postService.disLikePost(currentUser.id, item.id);
        dispatch(actions.setSelected(data));
      } catch (err) {
        dispatch(actions.error(err.response.data || err.message));
      }
    } else {
      console.log("in else block");
      try {
        const { data } = await postService.likePost(currentUser.id, item.id);
        dispatch(actions.setSelected(data));
      } catch (err) {
        dispatch(actions.error(err.response.data || err.message));
      }
    }
  };

  const setSelectedPost = (post) => {
    dispatch(actions.setSelected(post));
  };

  return (
    <PostContext.Provider
      value={{
        posts,
        selectedPost,
        loading,
        success,
        error,
        getPosts,
        addPost,
        handleLike,
        setSelectedPost,
      }}
    >
      {children}
    </PostContext.Provider>
  );
};

export default PostProvider;
