import actionTypes from "./action_types";

const loading = () => {
  return {
    type: actionTypes.LOADING,
  };
};

const error = (err) => {
  return {
    type: actionTypes.ERROR,
    payload: err,
  };
};

const addPost = (post) => {
  return {
    type: actionTypes.POST,
    payload: post,
  };
};

const updatePost = (post) => {
  return {
    type: actionTypes.UPDATE,
    payload: post,
  };
};

const deletePost = (post) => {
  return {
    type: actionTypes.DELETE,
    payload: post,
  };
};

const getPosts = (post) => {
  return {
    type: actionTypes.GET,
    payload: post,
  };
};

const setSelectedPost = (post) => {
  return {
    type: actionTypes.SET_SELECTED_POST,
    payload: post,
  };
};

const like = () => {
  return {
    type: actionTypes.LIKE,
  };
};

const disLike = () => {
  return {
    type: actionTypes.DISLIKE,
  };
};

const postActions = {
  loading,
  error,
  addPost,
  updatePost,
  deletePost,
  getPosts,
  setSelectedPost,
  like,
  disLike,
};

export default postActions;
