import React, { useContext, useState } from "react";
import UserContext from "../context/userContext";

const Home = () => {
  const [fisrtName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { user, loading, errors, register } = useContext(UserContext);
  // console.log();
  const handleFirstNameChange = ({ currentTarget: input }) => {
    setFirstName(input.value);
  };
  const handleLastNameChange = ({ currentTarget: input }) => {
    setLastName(input.value);
  };
  const handleEmailChange = ({ currentTarget: input }) => {
    setEmail(input.value);
  };
  const handlePasswordChange = ({ currentTarget: input }) => {
    setPassword(input.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      first_name: fisrtName,
      last_name: lastName,
      email: email,
      password: password,
    };

    register(newUser);
  };

  return (
    <div>
      Home
      <form action="POST">
        <label htmlFor="first_name">First Name:</label>
        <input
          type="text"
          name="first_name"
          id="first_name"
          placeholder="John"
          onChange={handleFirstNameChange}
        />
        <label htmlFor="last_name">Last Name:</label>
        <input
          type="text"
          name="last_name"
          id="last_name"
          placeholder="Doe"
          onChange={handleLastNameChange}
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          name="email"
          id="email"
          placeholder="Joh_doe@email.com"
          onChange={handleEmailChange}
        />
        <label htmlFor="password">Password</label>
        <input
          type="text"
          name="password"
          id="password"
          placeholder="Doe123@#"
          onChange={handlePasswordChange}
        />
        <button type="submit" onClick={handleSubmit}>
          Register
        </button>
      </form>
    </div>
  );
};

export default Home;
