import React, { useContext, useEffect, useState } from "react";
import {
  Container,
  Grid,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostCard from "../common/Card/index";
import PostContext from "../context/postContext";
import AlertBar from "../common/Alert";
import Loading from "../common/Loading";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
}));
const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  if (error)
    return (
      <AlertBar
        errMessage={error}
        title={error}
        severity={error ? "error" : "warning"}
      />
    );

  if (loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <Grid
          container
          spacing={2}
          justifyContent="center"
          alignItems="stretch"
        >
          {posts.map((post) => (
            <Grid item xs={3}>
              <PostCard key={post.id} post={post} loading={loading} />
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
};

export default Home;
