import React, { useContext, useEffect } from "react";
import {
  Container,
  Grid,
  CssBaseline,
  Typography,
  makeStyles,
} from "@material-ui/core";

import PostCard from "../common/Card/index";
import PostContext from "../context/postContext";
import AlertBar from "../common/Alert";
import Loading from "../common/Loading";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(15),
  },
}));
const Home = () => {
  const { posts, loading, error, success, getPosts } = useContext(PostContext);
  const classes = useStyles();

  useEffect(() => {
    getPosts();
  }, []);

  if (error)
    return (
      <AlertBar
        errMessage={error}
        title={error}
        severity={error ? "error" : "warning"}
      />
    );

  if (!success && loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Container maxWidth="lg" className={classes.root}>
        <Typography component="div">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <div
          className="posts-grid"

          // justifyContent="space-around"
          // alignContent="stretch"
        >
          {posts.map((post) => (
            <div className="post-grid-item" key={post.id}>
              <PostCard post={post} loading={loading} />
            </div>
          ))}
        </div>
      </Container>
    </>
  );
};

export default Home;
