import React, { useEffect, useContext } from "react";
import { CssBaseline, Typography } from "@material-ui/core";

import Posts from "../components/Posts";
import Layout from "../common/Layout";
import SnackBar from "../common/SnackBar";
import useSnackState from "../hooks/useSnackState";
import Loading from "../common/Loading";
import postActions from "../actions/postActions";
import postService from "../services/postsService";
import { PostContext } from "../context/postContext";

const Home = () => {
  const [state, dispatch] = useContext(PostContext);
  const [open, handleClose, handleOpen] = useSnackState();

  useEffect(() => {
    if (state.error) handleOpen();
    getPosts();
  }, [state.error]);

  const getPosts = async () => {
    try {
      dispatch(postActions.loading());

      const { data } = await postService.getAllPosts();

      dispatch(postActions.getPosts(data));
    } catch (err) {
      dispatch(postActions.error(err.response || err.message));
    }
  };

  const handleLike = async (user_id, post_id) => {
    try {
      dispatch(postActions.loading());
      dispatch(postActions.like(true));

      const { data } = await postService.likePost(user_id, post_id);

      dispatch(postActions.like(data));

      if (!state.like) {
        const { data } = await postService.disLikePost(user_id, post_id);

        dispatch(postActions.disLike(data));
        dispatch(postActions.disLike(false));
      }
    } catch (err) {
      dispatch(postActions.error(err.response.data || err.message));
    }
  };

  console.log("state: home: ", state);
  if (!state.success && state.loading) return <Loading />;
  return (
    <>
      <CssBaseline />
      <Layout>
        <Typography component="div" align="center">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <Posts items={state.posts} onLike={handleLike} like={state.like} />
      </Layout>
      {state.error && (
        <SnackBar
          open={open}
          severity={state.error ? "error" : "success"}
          err={state.error}
          success={state.success}
          onClose={handleClose}
        />
      )}
    </>
  );
};

export default Home;
