import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    flexBasis: theme.breakpoints.down(1000),
    alignContent: "stretch",
    alignItems: "stretch",
  },
  gridItems: {
    alignSelf: "auto",
  },
  title: {
    marginBottom: "10%",
  },
}));

export default useStyles;
