import { useState, useEffect, useContext } from "react";
import PropTypes from "prop-types";
import { Card, Button } from "@material-ui/core";
import {
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  IconButton,
  Typography,
  Link,
} from "@material-ui/core";
import ShareIcon from "@material-ui/icons/Share";
import formatDate from "../../utils/formatDate";
import getReadingTime from "../../utils/getReadingTime";
import Cardheader from "../CardHeader/CardHeader";
import formatSlug from "../../utils/formatSlug";
import Like from "../Like";
import useStyles from "./styles";
import { PostContext } from "../../context/postContext";

const PostCard = ({ item, onLike }) => {
  const { setSelectedPost } = useContext(PostContext);
  const [state, setState] = useState(item);
  const classes = useStyles();

  useEffect(() => {
    setState(item);
  }, [item]);
  return (
    <Card className={classes.root}>
      <Cardheader item={state} />
      <CardHeader title={state.title} subheader={formatDate(state.created)} />
      <CardMedia
        className={classes.media}
        image="/images/my-image.JPG"
        title={item.title}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {state.sub_title}
        </Typography>
      </CardContent>
      <CardActions disableSpacing className={classes.cardActions}>
        <Like item={state} onLike={onLike} />
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <Link href={`/posts/${formatSlug(state.title)}/${state.id}`}>
          <Button
            onClick={() => setSelectedPost(state)}
            size="small"
            color="primary"
          >
            Read
          </Button>
        </Link>
        <p>{getReadingTime(state.body)}</p>
      </CardActions>
    </Card>
  );
};

export default PostCard;

PostCard.propTypes = {
  item: PropTypes.object.isRequired,
  onLike: PropTypes.func.isRequired,
};
